CREATE VIEW "public"."hotmart_course_video_count" AS (select "hotmart_course_modules"."course", count("hotmart_video_assets"."id") as "count" from "hotmart_video_assets" left join "hotmart_course_lessons" on "hotmart_course_lessons"."id" = "hotmart_video_assets"."lesson" left join "hotmart_course_modules" on "hotmart_course_modules"."id" = "hotmart_course_lessons"."module" group by "hotmart_course_modules"."course");